package july;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=4;i++)
		{
			for(int j=1;j<=i;j++) {
				System.out.print("*");
		} System.out.println();
		}
			for(int k=1;k<=3;k++) {		
			 for(int g=3;g>=k;g--) {
				 System.out.print("*");
			 }
			 System.out.println();	
			 }
	}
	}


