package vaishali;

public class Pattern1 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++) //outer loop for no of rows
		 {
		 for(int j=1;j<=5;j++) //inner loop for no of columns 
		 {
		 System.out.print("*");
		 }
		 System.out.println(" ");
	}

}}
